mapper.insert:
insert into 
company_info(user_id, address, registration_number) 
values(#{userId}, #{address}, #{registrationNumber});

mapper.select:
select user_id, user_password
from user_info
where user_id = #{userId} and user_password = #{userPassword}

mapper.update:
update user_info
user_name = #{userName}, user_password = #{usrePassword}, user_nickname = #{userNickname}, user_phone = #{userPhone},
where user_id = #{userId}

update user_info
address = #{address}, registration_number = #{registrationNumber},
where user_id = #{userId}
